import React from 'react'
import { json,   useRouteLoaderData, redirect } from 'react-router-dom'
import EventItem from '../components/EventItem'

const EventDetailPage = () => {
    const loaderData = useRouteLoaderData('event-detail')
    // console.log(loaderData)
  return (
    <EventItem event={loaderData.event} />
  )
}

export default EventDetailPage

export const eventLoader = async ({request,params})=>{
    const id = params.id
    const response = await fetch(`http://localhost:8080/events/${id}`);
    if (!response.ok) {
        throw json({message: 'Could not fetch event'},{status: 500})
    }
    else{
        return response
    }

}

export const action = async ({request,params})=>{
  const id = params.id
  const response = await fetch(`http://localhost:8080/events/${id}`,{
    method: request.method
  });
  if (!response.ok) {
      throw json({message: 'Could not fetch delete event'},{status: 500})
  }
  else{
      return redirect('/events')
  }

}
